    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String paisBusca = paisTf.getText();
        paisLb.setText(paisBusca);
        capiLb.setText("Cagando...");
        capiLb.setText("Not Found");
        for(PaisCapis pc:paises){
            if(pc.getPais().compareToIgnoreCase(paisBusca) == 0)
                capiLb.setText(pc.getCapital());
        }
            
    }//GEN-LAST:event_jButton1ActionPerformed

